INSERT INTO `pins` VALUES (1,1,1,NULL,NULL,'gà con công nghiệp','gà con công nghiệp\n\n\nChan nuoi ga con nghiep','',1,300000,NULL,NULL,1,0,0,0,0,1,1,'2017-07-31 17:31:24','2017-08-12 04:10:23'),(2,2,2,NULL,NULL,'BỘ GENTEN-PLUS','THÀNH PHẦN\r\nTrong 1 chai:\r\n- Gentamicin sulfate\r\n- Amoxicillin trihydrate\r\n \r\nCÔNG DỤNG\r\n- Heo, bê nghé, dê cừu: Đặc trị viêm phổi cấp tính, tụ huyết trùng, viêm khớp, sưng phù đầu, viêm ruột - tiêu chảy.\r\n- Gà, vịt: Trị CRD, tụ huyết trùng, viêm khớp, viêm ruột - tiêu chảy.\r\n \r\nCÁCH DÙNG VÀ LIỀU DÙNG\r\nPha thuốc với chai SG.Vitamin B1+B6+B2 100ml đi kèm. Lắc kỹ trước khi tiêm. Tiêm bắp thịt liên tục từ 3 - 5 ngày, trường hợp bệnh nặng ngày đầu tiêm 2 lần, các ngày sau tiêm 1 lần.\r\n- Gà, vịt: 1ml/ 2 - 3kg thể trọng.\r\n- Heo con, bê nghé, dê cừu: 1ml/ 5kg thể trọng.\r\n \r\nLƯU Ý\r\nKhông tiêm quá 10ml tại mỗi vị trí tiêm.\r\nNgưng dùng thuốc 7 ngày trước khi giết mổ.','https://anovapharma.com/sptaca/san-pham-sg/san-pham-cho-gia-suc/thuoc-khang-sinh/bo-genten-plus.html',0,NULL,NULL,NULL,1,0,0,0,0,2,2,'2017-07-31 17:31:52','2017-08-14 01:15:32'),(3,2,1,1,NULL,'gà con công nghiệp','gà con công nghiệp','',NULL,NULL,NULL,NULL,0,0,2,0,0,2,NULL,'2017-07-31 17:43:29','2017-07-31 17:43:29'),(4,4,3,NULL,NULL,'APA CEFTIOFUR S','Phòng và trị các bệnh đường hô hấp trên heo như:\r\n– Viêm màng não (Streptococcus suis type 2).\r\n– Viêm phổi – màng phổi (Actinobacillus pleuropneumonia).\r\n– Tụ huyết trùng (Pasteurella multocida).\r\n– Viêm xoang ngực – xoang bụng (Haemophilus parasuis).\r\n– Phó thương hàn (Salmonella choleraesuis).','https://familala.com',0,NULL,NULL,NULL,0,0,0,0,0,4,4,'2017-08-01 01:31:23','2017-08-14 08:20:19'),(5,5,4,NULL,NULL,'Xương ống','Xương ống heo là phần xương của bắp chân con heo, thành phần chủ yếu là tủy sống, chứa nhiều canxi, vitamin A và chất béo. Xương ống thường dùng để hầm, ninh lấy nước dùng, chế biến nước dùng trong các món bún, phở, hủ tiếu…','',1,165000,NULL,NULL,0,0,0,1,1,5,5,'2017-08-01 01: